Z"},"trusted":true},"execution_count":null,"outputs":[]},{"cell_type":"code","source":"pairs.panels(female_frailty, cex.labels = 2)","metadata":{"execution":{"iopub.status.busy":"2023-09-20T16:24:00.961533Z","iopub.execute_input":"2023-09-20T16:24:00.963481Z","iopub.status.idle":"2023-09-20T16:24:01.364142Z"},"trusted":true},"execution_count":null,"outputs":[]},{"cell_type":"code","source":"my_corrplot(female_frailty)","metadata":{"execution":{"iopub.status.busy":"2023-09-20T16:24:02.068640Z","iopub.execute_input":"2023-09-20T16:24:02.070668Z","iopub.status.idle":"2023-09-20T16:24:02.258137Z"},"trusted":true},"execution_count":null,"outputs":[]},{"cell_type":"code","source":"par(mfrow = c(2, 2))\noptions(repr.plot.width = 5, repr.plot.height = 5)\nggplot(female_frailty, aes(x = Is_Frail_bool, y = Grip_strength_kg, color = as.factor(Is_Frail_bool))) +\n  geom_point(size = 4) +\n  xlab(\"Is frail\") +\n  ylab(\"Grip_strength_kg\") +\n  ggtitle(\"Grip Strength vs Frailty\")+\n  scale_color_discrete(name = \"Frailty Status\",\n                       labels = c(\"Not Frail\", \"Frail\"))\nggplot(female_frailty, aes(x = Is_Frail_bool, y = Weight_over_height_inpd, color = as.factor(Is_Frail_bool))) +\n  geom_point(size = 4) +\n  xlab(\"Is frail\") +\n  ylab(\"Weight_over_height_inpd\") +\n  ggtitle(\"Height/Weight vs Frailty\") +\n  scale_color_discrete(name = \"Frailty Status\",\n                       labels = c(\"Not Frail\", \"Frail\"))\nggplot(female_frailty, aes(x = Is_Frail_bool, y = Grip_strength_over_height, color = as.factor(Is_Frail_bool))) +\n  geom_point(size = 4) +\n  xlab(\"Is_Frail_bool\") +\n  ylab(\"Grip Strength divided by height\") +\n  ggtitle(\"Grip Strength divided by height vs Is_Frail_bool,\")+\n  scale_color_discrete(name = \"Frailty Status\",\n                       labels = c(\"Not Frail\", \"Frail\"))\nggplot(female_frailty, aes(x = Is_Frail_bool, y = Grip_strength_over_weight, color = as.factor(Is_Frail_bool))) +\n  geom_point(size = 4) +\n  x