student_performance$has_reduced_price_lunch, clean_student_performance$is_female),\n  col = colors,\n  xlab = \"\",\n  ylab = \"Combined scores\",\n  main = \"Combined scores\"\n)\n\nlegend(\"bottomright\", legend = \n       c(\"full price/male\", \"reduced price/male\", \"full price/female\", \"reduced price/female\"), \n       fill = colors, title = \"Lunch Price and gender\")\n","metadata":{"execution":{"iopub.status.busy":"2023-09-19T12:52:35.927221Z","iopub.execute_input":"2023-09-19T12:52:35.929297Z","iopub.status.idle":"2023-09-19T12:52:36.481753Z"},"trusted":true},"execution_count":null,"outputs":[]},{"cell_type":"code","source":"options(repr.plot.width = 10, repr.plot.height = 10)\n\n# Filter Combined_scores based on each one-hot encoded variable\nscores_var1 <- clean_student_performance$Combined_scores[clean_student_performance$race_A == 1]\nscores_var2 <- clean_student_performance$Combined_scores[clean_student_performance$race_B == 1]\nscores_var3 <- clean_student_performance$Combined_scores[clean_student_performance$race_C == 1]\nscores_var4 <- clean_student_performance$Combined_scores[clean_student_performance$race_D == 1]\nscores_var5 <- clean_student_performance$Combined_scores[clean_student_performance$race_E == 1]\n# Create a list\nlist_of_scores <- list(scores_var1, scores_var2, scores_var3, scores_var4, scores_var5)\ncolors <- c(\"yellow\", \"grey\", \"magenta\", \"blue\", 'green')\n# Create boxplot\nboxplot(\n  list_of_scores,\n  names = c(\"Race_A\", \"Race_B\", \"Race_C\", \"Race_D\", \"Race_E\"),\n  col = colors,\n  xlab = \"Race\",\n  ylab = \"Combined Scores\",\n  main = \"Boxplot of Combined Scores by Race\"\n)\nlegend(\"bottomright\", legend = \n       c(\"Race_A\", \"Race_B\", \"Race_C\", \"Race_D\", \"Race_E\"), \n       fill = colors, title = \"Combined Scores by Race\")","metadata":{"execution":{"iopub.status.busy":"2023-09-19T12:53:11.491363Z","iopub.execute_input":"2023-09-19T12:53:11.494198Z","iopub.status.idle":"2023-09-19T12:53:11.668128Z"},"trusted":true},"execution_count":null,"outputs":[]},{"cell_type":"code","source":"prop_func = function(race_var, ed_var) {\n  return(sum(clean_student_performance[[ed_var]][clean_student_performance[[race_var]] == 1]) /\n         sum(clean_student_performance[[race_var]] == 1))\n}\n\nrace_vars = c(\"race_A\", \"race_B\", \"race_C\", \"race_D\", \"race_E\")\ned_vars = c('parent_ed_hs_some', 'parent_ed_hs_all', 'parent_ed_col_some', 'parent_ed_col_4yr', 'parent_ed_col_6yr')\n\nproportions = data.frame(race = race_vars)\n\nfor (ed_var in ed_vars) {\n  proportions[[ed_var]] = sapply(race_vars, function(x) prop_func(x, ed_var))\n}\n\n# Transpose the data for plotting\ntransposed_data <- as.data.frame(t(proportions[,-1]))\ncolnames(transposed_data) <- proportions$race\n\n# Define custom labels for educational levels\ned_labels = c('HS (Some)', 'HS (All)', 'College (Some)', 'College (4Yr)', 'College (6Yr)')\n\n# Check for NAs\nprint(any(is.na(transposed_data)))\n\n# If all is good, plot\nif (!any(is.na(transposed_data))) {\n  # Increase the right margin for the legend\n  par(mar = c(5, 4, 4, 4))\n  \n  matplot(transposed_data, type = 'l', lty = 1, lwd = 2, col = 1:5, xlab = \"Educational Level\", ylab = \"Proportion\")\n  \n  # Add custom labels to the x-axis\n  axis(1, at=1:length(ed_labels), labels=ed_labels)\n  \n  legend(\"topright\", inset=c(0.1,0), legend = colnames(transposed_data), col = 1:5, lty = 1, lwd = 2)\n}\n\n\n\n","metadata":{"execution":{"iopub.status.busy":"2023-09-19T12:56:48.947032Z","iopub.execute_input":"2023-09-19T12:56:48.949229Z","iopub.status.idle":"2023-09-19T12:56:49.168639Z"},"trusted":true},"execution_count":null,"outputs":[]},{"cell_type":"code","source":"# Calculate proportions\nlunch_proportions <- sapply(race_vars, function(race_var) {\n  return(sum(clean_student_performance$has_reduced_price_lunch[clean_student_performance[[race_var]] == 1]) /\n         sum(clean_student_performance[[race_var]] == 1))\n})\n\n# Check for NAs\nprint(any(is.na(lunch_proportions)))\n\n# If all is good, plot\nif (!any(is.na(lunch_proportions))) {\n  barplot(lunch_proportions, names.arg = race_vars, xlab = \"Race\", ylab = \"Proportion with Reduced-Price Lunch\", col = colors)\n}\n","metadata":{"execution":{"iopub.status.busy":"2023-09-19T13:01:06.116985Z","iopub.execute_input":"2023-09-19T13:01:06.119596Z","iopub.status.idle":"2023-09-19T13:01:06.279547Z"},"trusted":true},"execution_count":null,"outputs":[]},{"cell_type":"code","source":"math_model = lm(score_math ~ is_female + has_reduced_price_lunch + taken_prep_course, data = clean_student_performance)\nrdg_model = lm(score_rdg ~ is_female + has_reduced_price_lunch + taken_prep_course, data = clean_student_performance)\nwrtng_model = lm(score_wrtng ~ is_female + has_reduced_price_lunch + taken_prep_course, data = clean_student_performance)\ncombined_model = lm(Combined_scores ~ is_female + has_reduced_price_lunch + taken_prep_course, data = clean_student_performance)","metadata":{"execution":{"iopub.status.busy":"2023-09-19T13:14:59.785314Z","iopub.execute_input":"2023-09-19T13:14:59.787980Z","iopub.status.idle":"2023-09-19T13:14:59.820059Z"},"trusted":true},"execution_count":null,"outputs":[]},{"cell_type":"code","source":"# Obtain predictions from the math model\nclean_student_performance$predicted_math <- predict(math_model, clean_student_performance)\n\n# Create the plot\nggplot(clean_student_performance, aes(x = predicted_math, y = score_math)) +\n  geom_point(aes(color = is_female)) + # Color by 'is_female' for additional insight\n  geom_smooth(method = \"lm\", se = FALSE, color = \"black\") +\n  geom_abline(intercept = 0, slope = 1, color = \"red\", linetype = \"dashed\") +\n  ggtitle(\"Predicted vs Actual Math Scores\") +\n  xlab(\"Predicted Scores\") +\n  ylab(\"Actual Scores\") \n\n","metadata":{"execution":{"iopub.status.busy":"2023-09-19T13:16:37.259341Z","iopub.execute_input":"2023-09-19T13:16:37.262069Z","iopub.status.idle":"2023-09-19T13:16:38.326502Z"},"trusted":true},"execution_count":null,"outputs":[]},{"cell_type":"code","source":"options(repr.plot.width = 10, repr.plot.height = 10)\n\n# Get model coefficients and tidy them up\nmath_model_tidy <- tidy(math_model)\n\n# Create coefficient plot\nggplot(math_model_tidy, aes(x = term, y = estimate)) +\n  geom_hline(yintercept = 0, linetype = \"dashed\", color = \"red\") +\n  geom_point(aes(color = term)) +\n  geom_errorbar(aes(ymin = estimate - std.error, ymax = estimate + std.error), width = 0.2) +\n  ggtitle(\"Coefficient Plot for Math Model\") +\n  xlab(\"Variables\") +\n  ylab(\"Coefficient Estimate\") +\n  theme_minimal()\n","metadata":{"execution":{"iopub.status.busy":"2023-09-19T13:20:26.620153Z","iopub.execute_input":"2023-09-19T13:20:26.626019Z","iopub.status.idle":"2023-09-19T13:20:27.357955Z"},"trusted":true},"execution_count":null,"outputs":[]},{"cell_type":"code","source":"","metadata":{},"execution_count":null,"outputs":[]}]}